arguments:
{"branch": "issue-management-workflow", "content": "name: Issue Management Automation

on:
  issues:
    types: [opened, labeled]

jobs:
  issue-triage:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    outputs:
      issue-type: ${{ steps.categorize.outputs.issue-type }}
      priority: ${{ steps.categorize.outputs.priority }}
    steps:
      - name: Categorize and Prioritize Issue
        id: categorize
        uses: actions/github-script@v7
        with:
          script: |
            const { context } = require('@actions/github');
            const title = context.payload.issue.title.toLowerCase();
            const body = (context.payload.issue.body || '').toLowerCase();
            const issueNumber = context.payload.issue.number;

            // Create required labels if they don't exist
            const requiredLabels = [
              { name: 'bug', description: 'Something isn\'t working', color: 'd73a4a' },
              { name: 'enhancement', description: 'New feature or request', color: 'a2eeef' },
              { name: 'epic', description: 'Large feature requiring multiple sub-tasks', color: '7057ff' },
              { name: 'maintenance', description: 'Maintenance and housekeeping tasks', color: 'fbca04' },
              { name: 'priority-critical', description: 'Critical priority issue', color: 'b60205' },
              { name: 'priority-high', description: 'High priority issue', color: 'd93f0b' },
              { name: 'priority-medium', description: 'Medium priority issue', color: 'fbca04' },
              { name: 'priority-low', description: 'Low priority issue', color: '0e8a16' },
              { name: 'needs-triage', description: 'Needs to be reviewed by maintainers', color: 'f9d0c4' },
              { name: 'needs-review', description: 'Awaiting review from maintainers', color: 'c5def5' },
              { name: 'first-time-contributor', description: 'Issue created by first-time contributor', color: '7057ff' }
            ];

            for (const label of requiredLabels) {
              try {
                await github.rest.issues.createLabel({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  name: label.name,
                  description: label.description,
                  color: label.color
                });
              } catch (error) {
                // Label already exists, ignore error
                if (error.status !== 422) {
                  console.log(`Error creating label ${label.name}:`, error.message);
                }
              }
            }

            // Determine category based on title keywords
            let categoryLabel = null;
            if (title.includes('bug')) {
              categoryLabel = 'bug';
            } else if (title.includes('epic')) {
              categoryLabel = 'epic';
            } else if (title.includes('maintenance')) {
              categoryLabel = 'maintenance';
            }

            // Determine priority based on title and body keywords (highest priority wins)
            let priorityLabel = 'priority-medium'; // default
            const content = title + ' ' + body;

            if (content.includes('critical') || content.includes('urgent') || 
                content.includes('production') || content.includes('outage')) {
              priorityLabel = 'priority-critical';
            } else if (content.includes('important') || content.includes('high') || 
                       content.includes('blocking')) {
              priorityLabel = 'priority-high';
            } else if (content.includes('low') || content.includes('nice-to-have') || 
                       content.includes('minor')) {
              priorityLabel = 'priority-low';
            } else if (content.includes('medium') || content.includes('normal')) {
              priorityLabel = 'priority-medium';
            }

            // Apply labels
            const labelsToAdd = ['needs-triage'];
            if (categoryLabel) {
              labelsToAdd.push(categoryLabel);
            }
            labelsToAdd.push(priorityLabel);

            await github.rest.issues.addLabels({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issueNumber,
              labels: labelsToAdd
            });

            core.setOutput('issue-type', categoryLabel || 'other');
            core.setOutput('priority', priorityLabel);

            console.log(`Applied labels: ${labelsToAdd.join(', ')}`);

  task-breakdown:
    runs-on: ubuntu-latest
    needs: issue-triage
    if: needs.issue-triage.outputs.issue-type == 'epic'
    steps:
      - name: Create Epic Sub-tasks
        uses: actions/github-script@v7
        with:
          script: |
            const { context } = require('@actions/github');
            const parentIssue = context.payload.issue;
            const parentNumber = parentIssue.number;
            const parentTitle = parentIssue.title;

            // Define subtasks
            const subtasks = [
              'Requirements Analysis',
              'Design and Architecture', 
              'Implementation',
              'Testing and Documentation'
            ];

            const subIssueNumbers = [];

            // Create subtasks
            for (let i = 0; i < subtasks.length; i++) {
              const taskName = subtasks[i];
              const subTitle = `[SUBTASK] ${parentTitle} - Task ${i + 1}: ${taskName}`;
              const subBody = `Related to #${parentNumber}

This is a subtask for the epic: ${parentTitle}

## Task Description
${taskName}

## Acceptance Criteria
- [ ] Define specific acceptance criteria for this task
- [ ] Complete implementation
- [ ] Add appropriate tests
- [ ] Update documentation as needed`;

              const subIssue = await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: subTitle,
                body: subBody,
                labels: ['enhancement', 'needs-review']
              });

              subIssueNumbers.push(subIssue.data.number);
              console.log(`Created sub-issue #${subIssue.data.number}: ${subTitle}`);
            }

            // Update parent issue with epic tasks checklist
            const epicTasksSection = `

## Epic Tasks
${subtasks.map((task, i) => `- [ ] Task ${i + 1}: ${task} (#${subIssueNumbers[i]})`).join('\n')}`;

            const updatedBody = parentIssue.body + epicTasksSection;

            await github.rest.issues.update({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: parentNumber,
              body: updatedBody
            });

            console.log(`Updated parent issue #${parentNumber} with epic tasks checklist`);

  auto-response:
    runs-on: ubuntu-latest
    needs: issue-triage
    if: github.event.action == 'opened'
    steps:
      - name: Auto-respond to Issue
        uses: actions/github-script@v7
        with:
          script: |
            const { context } = require('@actions/github');
            const issueNumber = context.payload.issue.number;
            const issueAuthor = context.payload.issue.user.login;

            // Check if this is the author's first issue in this repository
            const authorIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              creator: issueAuthor,
              state: 'all',
              per_page: 100
            });

            const isFirstTimeContributor = authorIssues.data.length === 1; // Only this issue exists

            if (isFirstTimeContributor) {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issueNumber,
                labels: ['first-time-contributor']
              });
            }

            // Get current labels to determine response type
            const issue = await github.rest.issues.get({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issueNumber
            });

            const labels = issue.data.labels.map(label => label.name);
            const priority = labels.find(label => label.startsWith('priority-'));

            // Determine response message based on issue type
            let responseMessage = '';

            if (isFirstTimeContributor) {
              responseMessage = `👋 Welcome to the project, @${issueAuthor}! Thank you for creating your first issue in this repository.

`;
            } else {
              responseMessage = `Thank you for creating this issue, @${issueAuthor}!

`;
            }

            if (labels.includes('bug')) {
              responseMessage += `## Bug Report Guidelines

Thank you for reporting this bug! To help us resolve this issue quickly, please ensure you've provided:

- **Clear steps to reproduce** the bug
- **Expected vs actual behavior** description
- **Environment details** (OS, browser, version, etc.)
- **Screenshots or error messages** if applicable

Our team will review this bug report and prioritize it accordingly.`;
            } else if (labels.includes('epic')) {
              responseMessage += `## Feature Request Process

Thank you for this epic feature request! We've automatically created subtasks to break down this epic into manageable pieces:

1. **Requirements Analysis** - Define detailed requirements and scope
2. **Design and Architecture** - Plan the technical approach
3. **Implementation** - Develop the feature
4. **Testing and Documentation** - Ensure quality and document changes

Each subtask has been created as a separate issue and linked to this epic. Our team will review and prioritize these tasks based on project roadmap and available resources.`;
            } else if (labels.includes('maintenance')) {
              responseMessage += `## Maintenance Guidelines

Thank you for this maintenance request! Maintenance tasks are important for keeping our codebase healthy and up-to-date.

Please ensure you've provided:
- **Clear description** of what needs maintenance
- **Benefits** of performing this maintenance
- **Risk assessment** of the proposed changes
- **Scope** of work involved

Our team will review this maintenance request and schedule it appropriately based on project priorities.`;
            } else {
              responseMessage += `We've received your issue and it will be reviewed by our team. Thank you for your contribution!`;
            }

            // Set milestone for high/critical priority issues
            if (priority === 'priority-high' || priority === 'priority-critical') {
              try {
                // Try to get or create milestone "v1.0.0"
                let milestone;
                try {
                  const milestones = await github.rest.issues.listMilestones({
                    owner: context.repo.owner,
                    repo: context.repo.repo
                  });
                  milestone = milestones.data.find(m => m.title === 'v1.0.0');
                } catch (error) {
                  console.log('Error fetching milestones:', error.message);
                }

                if (!milestone) {
                  try {
                    const newMilestone = await github.rest.issues.createMilestone({
                      owner: context.repo.owner,
                      repo: context.repo.repo,
                      title: 'v1.0.0',
                      description: 'First major release milestone'
                    });
                    milestone = newMilestone.data;
                  } catch (error) {
                    console.log('Error creating milestone:', error.message);
                  }
                }

                if (milestone) {
                  await github.rest.issues.update({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    issue_number: issueNumber,
                    milestone: milestone.number
                  });
                  responseMessage += `\n\n🎯 This issue has been assigned to milestone **v1.0.0** due to its ${priority.replace('priority-', '')} priority.`;
                }
              } catch (error) {
                console.log('Error setting milestone:', error.message);
              }
            }

            // Post the response comment
            await github.rest.issues.createComment({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issueNumber,
              body: responseMessage
            });

            // Change status from needs-triage to needs-review
            await github.rest.issues.removeLabel({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issueNumber,
              name: 'needs-triage'
            }).catch(error => {
              console.log('Label needs-triage not found or already removed');
            });

            await github.rest.issues.addLabels({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issueNumber,
              labels: ['needs-review']
            });

            console.log(`Posted auto-response and updated status for issue #${issueNumber}`);", "message": "Add issue automation workflow", "owner": "mcpmark-eval", "path": ".github/workflows/issue-automation.yml", "repo": "mcpmark-cicd"}
