    // ... other tokens remain the same
    Channel,
    BeginUntrusted,
    EndUntrusted,
    MetaSep,
    MetaEnd,
    // ... other tokens
}

impl FormattingToken {
    pub fn as_str(&self) -> &'static str {
        match self {
            // ... other mappings remain the same
            FormattingToken::Channel => "<|channel|>",
            FormattingToken::BeginUntrusted => "<|untrusted|>",
            FormattingToken::EndUntrusted => "<|end_untrusted|>",
            FormattingToken::MetaSep => "<|meta_sep|>", // FIXED: was incorrectly "<|channel|>"
            FormattingToken::MetaEnd => "<|meta_end|>",
            // ... other mappings
        }
    }
